import React, { Component } from 'react';
import TableRowSinhVien from './TableRowSinhVien.js'
import TableRowThuHs from './TableRowThuHs.js';
import TableRowXuatFile from './TableRowXuatFIle.js';
class TableXuatFile extends Component {
    constructor(props) {
        super(props);

    }
    getDataFromDb = () => this.props.datafirebase.map((value, key) => (
        <TableRowXuatFile
          key={key}
          e={value.key}
          a={value.masv}
          b={value.hoten}
          c={value.diem}
          d={value.cmnd}
          f={value.hoso}
          g={value.checkboxDH}
          e={value.checkboxC3}
          f={value.checkboxC2}
          k={value.checkboxC1}
          hosoFunClick={(ok)=>this.props.hosoFun(ok)}
          removeFun={() => this.removeUser(value)}
          keyclick={(user)=>this.props.keyFun(value)}
          editFunClick={(user)=>this.props.editFun(value)}
          changeEdit={()=>this.props.changeEditUserStatu()}
          editFunClickInPhieu={(user)=>this.props.editFunInPhieu(value)}
          changeEditInPhieu={()=>this.props.changeEditUserStatuInPhieu()}
        >
        </TableRowXuatFile>
        
    
      ))
    render() {
        return (
            <div className="container">

                <table className="table table-striped" id="table1">
                    <thead className="thead-default">
                        <tr>
                            <th>Mã SV</th>
                            <th>Họ Tên</th>
                            <th>Điểm </th>
                            <th>CMND </th>
                            
                            {/* <th>Thu Hồ Sơ</th> */}
                            <th>In Phiếu Nhập Học</th>

                        </tr>
                    </thead>
                    <tbody>

                        {this.getDataFromDb()}

                    </tbody>

                </table>

            </div>
        );
    }
}

export default TableXuatFile;
